mapper.select:

SELECT approval_form as "approvalForm"
FROM approval_category
WHERE approval_category_id = #{approvalCategoryId}

SELECT count(*)
FROM approval
WHERE temp_approval='n' and emp_id = #{empId}

		AND status = #{status}

SELECT approval_id as "approvalId", title as "title", a.emp_id as "empId", name as "empName", write_date as "writeDate", a.approval_category_id as "approvalCategoryId", approval_name as "categoryName", status, temp_approval as "tempApproval" FROM approval a, employees e, approval_category c WHERE a.emp_id=e.emp_id and a.approval_category_id=c.approval_category_id and temp_approval='n' and a.emp_id = #{empId}
AND status = #{status}
ORDER BY write_date desc OFFSET #{pager.startRowNo}-1 ROWS FETCH FIRST 10 ROWS ONLY

SELECT count(*)
FROM approval
WHERE temp_approval='y' and emp_id = #{empId}
