mapper.select:
SELECT approval_id as "approvalId", title as "title", a.emp_id as "empId", name as "empName", write_date as "writeDate", approval_category_id as "approvalCategoryId", status, temp_approval as "tempApproval" FROM approval a, employees e WHERE temp_approval='n' and a.emp_id = #{empId}
AND status = #{status}
ORDER BY write_date desc OFFSET #{pager.startRowNo}-1 ROWS FETCH FIRST 10 ROWS ONLY

SELECT count(*)
FROM approval a
WHERE temp_approval='n' and emp_id = #{empId}

		AND status = #{status}
