mapper.select:
SELECT count(*)
FROM vacation
WHERE 

		emp_id = #{empId}


		approval_emp_id = #{empId}


		AND status = #{status}

select v.vacation_id as "vacationId", e.name as "name", e.emp_id as "empId", c.vacation_name as "vacationName", status, count_day as "countDay", to_char((SELECT * FROM ( select start_date from vacation_date where vacation_id = v.vacation_id order by start_date ) WHERE ROWNUM = 1),'yyyy-mm-dd') "startDate", to_char((SELECT * FROM ( select start_date from vacation_date where vacation_id = v.vacation_id order by start_date desc ) WHERE ROWNUM = 1),'yyyy-mm-dd') "endDate"
from vacation v, employees e, vacation_category c where
v.emp_id = #{empId}
v.approval_emp_id = #{empId}
and e.emp_id = v.emp_id and c.vacation_category_id = v.vacation_category_id
AND status = #{status}
order by vacation_id desc OFFSET #{pager.startRowNo}-1 ROWS FETCH FIRST 10 ROWS ONLY

select dayoff_remain as dayoffRemain , add_dayoff_remain as addDayoffRemain
from employees e 
where e.emp_id = #{empId}
